pragma solidity <0.8.0;

interface IERC20 {
    // return total tokens in exisitence
    function totalSupply() external view returns (uint256);

    // return the amount of tokens owned by 'account'
    function balanceOf(address account) external view returns (uint256);

    // send amount of tokens to another
    function transfer(address to, uint256 amount) external returns (bool);

    // return number of tokens that 'spender' will be allowed to spend on behalf of 'owner' thru {transferFrom}
    // default is 0
    // change when {aprrove} or {transferFrom} are called
    function allowance(address owner, address spender)
        external
        view
        returns (uint256);

    // set 'amount' as the allowance of `spender` over the caller's tokens
    // return a boolean value indicating whether the operation succeeded
    function approve(address spender, uint256 amount) external returns (bool);

    // send 'amount' of tokens from 'owner' to 'recipient' using allowance mechanism
    // allow smart contract to execute a transfer on behalf of 'owner'
    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) external returns (bool);

    // emit when 'value' tokens are moved from 'from' to 'to'
    event Transfer(address indexed from, address indexed to, uint256 value);
    //emit when the allowance of 'spender' for an 'owner' is set by a call to {aprrove}
    event Approval(address indexed owner, address indexed to, uint256 value);
}

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: Addition overflow");

        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a - b;
        require(b <= a, "SafeMath: Subtract overflow");

        return c;
    }

    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0 || b == 0) return 0;

        uint256 c = a * b;

        require(c / a == b, "SafeMath: Multiplication overflow");

        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        require(b > 0, "SafeMath: Division by zero");
        uint256 c = a / b;

        return c;
    }
}

contract ERC20Token is IERC20 {
    using SafeMath for uint256;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;
    string private _name;
    string private _symbol;
    uint32 private _decimals;

    constructor(
        uint256 totalSupply,
        string memory name,
        string memory symbol,
        uint32 decimals
    ) public {
        _totalSupply = totalSupply;
        _name = name;
        _symbol = symbol;
        _decimals = decimals;

        _balances[msg.sender] = totalSupply;
    }

    /** internal functions  **/

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal {
        require(
            amount < _balances[from],
            "Error: balances is not enough to operate"
        );
        _balances[from] = _balances[from].sub(amount);
        _balances[to] = _balances[to].add(amount);

        emit Transfer(from, to, amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal {
        _allowances[owner][spender] = amount;

        emit Approval(owner, spender, amount);
    }

    function totalSupply() public override view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public override view returns (uint256) {
        return _balances[account];
    }

    function transfer(address to, uint256 amount)
        public
        override
        returns (bool)
    {
        _transfer(msg.sender, to, amount);

        return true;
    }

    function allowance(address owner, address spender)
        public
        override
        view
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        public
        override
        returns (bool)
    {
        _approve(msg.sender, spender, amount);

        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public override returns (bool) {
        _transfer(from, to, amount);
        _approve(from, msg.sender, _allowances[from][msg.sender].sub(amount));

        return true;
    }
}
